import Home from "./components/Home/Home";
import React, { useState, useEffect } from "react";
import AuthContext from "./components/Ask/Contexts/AuthContext";
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Redirect
} from "react-router-dom";
import axios from "axios";
import Navbar from "./components/Ask/Components/Navbar";
import Login from "./components/Ask/Pages/Auth/Login";
import Register from "./components/Ask/Pages/Auth/Register";
import CreateCategory from "./components/Ask/Pages/Category/CreateCategory";
import BrowseCategories from "./components/Ask/Pages/Category/BrowseCategories";
import ShowCategory from "./components/Ask/Pages/Category/ShowCategory";
import CreateForum from "./components/Ask/Pages/Forum/CreateForum";
import ShowForum from "./components/Ask/Pages/Forum/ShowForum";
import CreateThread from "./components/Ask/Pages/Thread/CreateThread";
import ShowThread from "./components/Ask/Pages/Thread/ShowThread";

function App() {
  const [user, setUser] = useState(null);
  const [isInitiated, setIsInitiated] = useState(false);

  useEffect(() => {
    init();
  }, []);

  const init = async () => {
    const token = localStorage.getItem("token");
    const response = await axios.get("/api/auth/init", { params: { token } });
    const { user } = response.data;
    setUser(user);
    setIsInitiated(true);
  };

  const handleLogout = () => {
    setUser(null);
    localStorage.setItem("token", null);
  };

  return (
    <div>
      {isInitiated && (
        <AuthContext.Provider value={{ user, setUser, handleLogout }}>
          <Router>
            <Navbar />
            <Switch>
              <Header />
              <Route path="/" exact component={Home} />
              <Route path="/SchoolReviews" exact component={Resources} />
              <Route path="/SchoolReviews/UMich" exact component={UMich} />
              <Route
                path="/SchoolReviews/SouthernCalifornia"
                exact
                component={SouthernCalifornia}
              />
              <Route
                path="/SchoolReviews/Washington"
                exact
                component={Washington}
              />
              <Route path="/SchoolReviews/NYU" exact component={NYU} />
              <Route
                path="/SchoolReviews/UTAustin"
                exact
                component={UTAustin}
              />
              <Route path="/SchoolReviews/Aarhus" exact component={Aarhus} />
              <Route
                path="/SchoolReviews/Copenhagen"
                exact
                component={Copenhagen}
              />
              <Route path="/SchoolReviews/Lund" exact component={Lund} />
              <Route path="/SchoolReviews/Monash" exact component={Monash} />
              <Route
                path="/SchoolReviews/UNSWSydney"
                exact
                component={UNSWSydney}
              />
              <Route path="/SchoolReviews/Sydney" exact component={Sydney} />
              <Route path="/SchoolReviews/Korea" exact component={Korea} />
              <Route
                path="/SchoolReviews/HECMontreal"
                exact
                component={HECMontreal}
              />
              <Route path="/SchoolReviews/York" exact component={York} />
              <Route path="/SchoolReviews/Queens" exact component={Queens} />

              <Footer />
            </Switch>
          </Router>
        </AuthContext.Provider>
      )}
    </div>
  );
}

export default App;

