import Header from "./components/Header/Header";
import Footer from "./components/Footer/Footer";
import Resources from "./components/SchoolReviews/Resources";

//Canada
import HECMontreal from "./components/SchoolReviews/Canada/HECMontreal/HECMontreal";
import York from "./components/SchoolReviews/Canada/York/York";
import Queens from "./components/SchoolReviews/Canada/Queens/Queens";

//Asia, SouthKorea
import Korea from "./components/SchoolReviews/Asia/SouthKorea/Korea/Korea";

//Australia
import Monash from "./components/SchoolReviews/Australia/Monash/Monash";
import Sydney from "./components/SchoolReviews/Australia/Sydney/Sydney";
import UNSWSydney from "./components/SchoolReviews/Australia/UNSWSydney/UNSWSydney";

//Europe
import Aarhus from "./components/SchoolReviews/Europe/Aarhus/Aarhus";
import Copenhagen from "./components/SchoolReviews/Europe/Copenhagen/Copenhagen";
import Lund from "./components/SchoolReviews/Europe/Lund/Lund";

//United States
import UMich from "./components/SchoolReviews/United States/UMich/UMich";
import SouthernCalifornia from "./components/SchoolReviews/United States/SouthernCalifornia/SouthernCalifornia";
import Washington from "./components/SchoolReviews/United States/Washington/Washington";
import UTAustin from "./components/SchoolReviews/United States/UTAustin/UTAustin";
import NYU from "./components/SchoolReviews/United States/NYU/NYU";
import Home from "./components/Home/Home";
import React, { useState, useEffect } from "react";
import AuthContext from "./components/Ask/Contexts/AuthContext";
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Redirect
} from "react-router-dom";
import axios from "axios";
import Navbar from "./components/Ask/Components/Navbar";
import Login from "./components/Ask/Pages/Auth/Login";
import Register from "./components/Ask/Pages/Auth/Register";
import CreateCategory from "./components/Ask/Pages/Category/CreateCategory";
import BrowseCategories from "./components/Ask/Pages/Category/BrowseCategories";
import ShowCategory from "./components/Ask/Pages/Category/ShowCategory";
import CreateForum from "./components/Ask/Pages/Forum/CreateForum";
import ShowForum from "./components/Ask/Pages/Forum/ShowForum";
import CreateThread from "./components/Ask/Pages/Thread/CreateThread";
import ShowThread from "./components/Ask/Pages/Thread/ShowThread";

function App() {
  const [user, setUser] = useState(null);
  const [isInitiated, setIsInitiated] = useState(false);

  useEffect(() => {
    init();
  }, []);

  const init = async () => {
    const token = localStorage.getItem("token");
    const response = await axios.get("/api/auth/init", { params: { token } });
    const { user } = response.data;
    setUser(user);
    setIsInitiated(true);
  };

  const handleLogout = () => {
    setUser(null);
    localStorage.setItem("token", null);
  };

  return (
    <div>
      {isInitiated && (
        <AuthContext.Provider value={{ user, setUser, handleLogout }}>
          <Router>
            <Navbar />
            <Switch>
              <Route path="/" exact>
                <Home />
              </Route>
              <Route path="/auth/login">
                {!user ? <Login /> : <Redirect to="/" />}
              </Route>
              <Route path="/auth/register">
                {!user ? <Register /> : <Redirect to="/" />}
              </Route>
              <Route path="/category/create">
                {user ? <CreateCategory /> : <Redirect to="/auth/login" />}
              </Route>
              <Route path="/category/:id">
                <ShowCategory />
              </Route>
              <Route path="/category">
                <BrowseCategories />
              </Route>
              <Route path="/forum/create/:id">
                {user ? <CreateForum /> : <Redirect to="/auth/login" />}
              </Route>
              <Route path="/forum/:id">
                <ShowForum />
              </Route>
              <Route path="/thread/create/:id">
                {user ? <CreateThread /> : <Redirect to="/auth/login" />}
              </Route>
              <Route path="/thread/:id">
                <ShowThread />
              </Route>
            </Switch>
          </Router>
        </AuthContext.Provider>
      )}
    </div>
  );
}

export default App;
