import React, { useEffect, useState } from "react";
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
import axios from "axios";
import AppContext from "./components/Ask/Contexts/AppContext";
import Home from "./components/Ask/Pages/Home";


function App() {
  const [user, setUser] = useState(null);
  const [isInitiated, setIsInitiated] = useState(false);

  useEffect(() => {
    init();
  }, []);

  const init = async () => {
    const token = localStorage.getItem("token");
    const response = await axios.get("/api/auth/init", { params: { token } });
    const { user } = response.data;
    setUser(user);
    setIsInitiated(true);
  };

  return (
    <div>
      {isInitiated && (
        <AppContext.Provider value={{ user, setUser }}>
          <Router>
            <Switch>
              <Header />
              <Route path="/" exact component={Home} />
              <Route path="/SchoolReviews" exact component={About} />
              <Route path="/SchoolReviews/UMich" exact component={UMich} />
              <Route path="/SchoolReviews/Aarhus" exact component={Aarhus} />
              <Route path="/MappingPlanner" exact component={MappingPlanner} />
              <Route path="/Backpack" exact component={Backpack} />
              <Route path="/Ask" exact component={Ask} />
              <Footer />
            </Switch>
          </Router>
        </AppContext.Provider>
      )}
    </div>
  );
}

export default App;
